    private void btnCộngActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnCộngActionPerformed
        double a = Double.parseDouble(txtA.getText());
        double b = Double.parseDouble(txtB.getText());
        double Tong = a+b;
        txtPhepToan.setText("+");
        txtKq.setText(String.valueOf (Tong));
    }//GEN-LAST:event_btnCộngActionPerformed

    private void btnTruActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnTruActionPerformed
        double a = Double.parseDouble(txtA.getText());
        double b = Double.parseDouble(txtB.getText());
        double Tru = a-b;
        txtPhepToan.setText("-");
        txtKq.setText(String.valueOf (Tru));
    }//GEN-LAST:event_btnTruActionPerformed

