    private void btngiaiActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btngiaiActionPerformed
       double a1 = Double.parseDouble(txta1.getText());
       double b1 = Double.parseDouble(txtb1.getText());
       double c1 = Double.parseDouble(txtc1.getText());
       double a2 = Double.parseDouble(txta2.getText());
       double b2 = Double.parseDouble(txtb2.getText());
       double c2 = Double.parseDouble(txtc2.getText());
       double d = (a2*b1) - (a1*b2);
       double dx = (c2*b1) - (c1*b2);
       double dy = (a2*c1) - (a1*c2);
       if(d !=0)
       {
           if((dx != dy) && (dx != 0) && (dy != 0))
           {
               double x = dx/d;
               double y = dy/d;
               txtkqx.setText(String.valueOf(x));
               txtkqy.setText(String.valueOf(y));
           }
           else if((dx == dy))
           {
                if((dx !=0) && (dy !=0))
                {
                    double x = dx/d;
                    double y = dy/d;
                    txtkqx.setText(String.valueOf(x));
                    txtkqy.setText(String.valueOf(y));
                }
            
                else txtkqx.setText(" hệ phương trình có vô số nghiệm");     
           }
       }
        else txtkqx.setText("hệ phương trình vô nghiệm");
    }//GEN-LAST:event_btngiaiActionPerformed

